package javaPackage;

public class IfElse2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 777;
		int b = 777;
		
		if(a<b) {
			System.out.println("a is lesser");
		}else if(a == b) {
			System.out.println("a is equal b");
		}else {
			System.out.println("b is lesser");
		}
	}

}
